INSERT INTO `dede_archives` (`id`, `typeid`, `typeid2`, `sortrank`, `flag`, `ismake`, `channel`, `arcrank`, `click`, `money`, `title`, `shorttitle`, `color`, `writer`, `source`, `litpic`, `pubdate`, `senddate`, `mid`, `keywords`, `lastpost`, `scores`, `goodpost`, `badpost`, `voteid`, `notpost`, `description`, `filename`, `dutyadmin`, `tackid`, `mtype`, `weight`) VALUES
(1, 10, '0', 1624188383, 'p', 1, 1, 0, 59, 0, '一切反动派都是纸老虎', '', '', 'admin', '未知', '/A067477/uploads/210620/1-210620164911342.jpg', 1624188383, 1624173223, 1, '一切,反动派,都是,纸老虎,', 0, 0, 0, 0, 0, 0, '1946年8月6日，毛泽东在延安杨家岭接受了美国记者安娜路易斯斯特朗的采访。面对敌强我弱的严峻形势，毛泽东满怀信心地提出一切反动派都是纸老虎的著名论断。', '', 1, 0, 0, 0),
(2, 4, '0', 1624174798, 'p', 1, 1, 0, 84, 0, '学史明理 明理悟道', '', '', '慎海雄', '《求是》', '/A067477/uploads/210620/1-210620154131340.jpg', 1624174798, 1624174997, 1, '学史,明理,悟道,学史,明理,悟道,慎海,雄,历史,', 0, 0, 0, 0, 0, 0, '“历史是最好的老师”。中国共产党的百年苦难辉煌，是人类历史上一部感天动地的壮丽史诗。学好党史这门必修课，是重大现实课题，更是每一名党员干部的终身课题。“心有所信，', '', 1, 0, 0, 1),
(3, 4, '0', 1624175002, 'p', 1, 1, 0, 167, 0, '以史明志，吹响新征程的嘹亮号角', '', '', '铁 凝', '《求是》', '/A067477/uploads/210620/1-210620154413X8.jpg', 1624175002, 1624175086, 1, '以,史明志,史,明志,吹响,新征程,新,征程,的,', 0, 0, 0, 0, 0, 0, '中华民族正在迎来一个庄严、重大、喜庆的日子：2021年7月1日，中国共产党成立一百周年。', '', 1, 0, 0, 2),
(4, 4, '0', 1624175089, 'p', 1, 1, 0, 92, 0, '开启社会保障事业高质量发展新征程', '', '', '张纪南', '《求是》', '/A067477/uploads/210620/1-210620154602R5.jpg', 1624175089, 1624175176, 1, '开启,社会保障,事业,高质量,发展,新征程,新,', 0, 0, 0, 0, 0, 0, '习近平总书记在中央政治局第二十八次集体学习时发表重要讲话，强调社会保障是保障和改善民生、维护社会公平、增进人民福祉的基本制度保障，是促进经济社会发展、实现广大人民', '', 1, 0, 0, 3),
(5, 4, '0', 1624175183, 'p', 1, 1, 0, 60, 0, '为全面建成小康社会提供水利支撑', '', '', '中共水利部党组', '《求是》', '/A067477/uploads/allimg/210620/1549305Z3-0-lp.jpg', 1624175183, 1624175370, 1, '为,全面,建成,小康社会,提供,水利,支撑,为,', 0, 0, 0, 0, 0, 0, '水的问题能不能解决好，直接关系到小康路上群众的幸福感、获得感、安全感。习近平总书记指出：“全面建成小康社会，关键是要把经济社会发展的‘短板’尽快补上，否则就会贻误', '', 1, 0, 0, 4),
(6, 4, '0', 1624175374, 'p', 1, 1, 0, 82, 0, '改革不停顿 开放不止步', '', '', '王灵桂', '《求是》', '/A067477/uploads/210620/1-210620155121O5.jpg', 1624175374, 1624175511, 1, '改革,不停顿,开放,不,止步,改革,不停顿,开放,', 0, 0, 0, 0, 0, 0, '推进改革开放和中国特色社会主义事业，是近代以来与建立中国共产党、成立中华人民共和国并立的实现中华民族伟大复兴的三大里程碑。学习改革开放史，要求我们深刻认识改革开放', '', 1, 0, 0, 5),
(7, 4, '0', 1624175514, 'p', 1, 1, 0, 177, 0, '深化粤港澳合作 推进大湾区建设', '', '', '何立峰', '《求是》', '/A067477/uploads/210620/1-210620155R5E5.jpg', 1624175514, 1624175916, 1, '深化,粤港澳,合作,推进,大湾区,建设,深化,', 0, 0, 0, 0, 0, 0, '粤港澳大湾区是我国开放程度最高、经济活力最强的区域之一，在国家发展大局中具有重要战略地位。推进粤港澳大湾区建设，是习近平总书记亲自谋划、亲自部署、亲自推动的重大国', '', 1, 0, 0, 6),
(8, 4, '0', 1624176065, 'p', 1, 1, 0, 118, 0, '新征程上文艺工作的光辉指南', '', '', '李 屹', '《求是》', '/A067477/uploads/210620/1-210620160222319.jpg', 1624176065, 1624176149, 1, '新征程,新,征程,上,文艺,工作,的,光辉,指南,', 0, 0, 0, 0, 0, 0, '在迎接建党百年的日子里，革命历史题材电视剧《觉醒年代》热播，让人们深刻感受到真理的光芒、理想的魅力。', '', 1, 0, 0, 7),
(9, 4, '0', 1624176153, 'p', 1, 1, 0, 99, 0, '真理之光绽放', '', '', '刘慧晏', '《求是》', '/A067477/uploads/210620/1-210620160333633.jpg', 1624176153, 1624176227, 1, '真理,之光,绽放,真理,之光,绽放,写在,《,', 0, 0, 0, 0, 0, 0, '马克思主义是科学的、人民的、实践的、不断发展的开放的理论，不仅深刻改变了世界，也深刻改变了中国。', '', 1, 0, 0, 8),
(10, 4, '0', 1624176230, 'p', 1, 1, 0, 102, 0, '以首善标准书写革命文物保护利用新篇章', '', '', '中共北京市委理论', '《求是》', '/A067477/uploads/210620/1-2106201605094b.jpg', 1624176230, 1624176348, 1, '以,首善,标准,书写,革命,文物保护,利用,以,', 0, 0, 0, 0, 0, 0, '革命文物承载着党和人民英勇奋斗的光荣历史，记载着中国革命的伟大历程和感人事迹，是党和国家的宝贵财富，是弘扬革命传统和革命文化、加强社会主义精神文明建设、激发爱国热', '', 1, 0, 0, 9),
(11, 4, '0', 1624176353, 'p', 1, 1, 0, 139, 0, '从党的光辉历史中汲取砥砺奋进的精神力量', '', '', '中共湖南省委', '《求是》', '/A067477/uploads/210620/1-210620160RRO.jpg', 1624176353, 1624176513, 1, '从,党的,光辉,历史,中,汲取,砥砺,奋进,的,从,', 0, 0, 0, 0, 0, 0, '湖南是毛泽东同志的家乡、老一辈无产阶级革命家集中的省份。党的十八大以来，习近平总书记三次考察湖南，对湖南的党史资源和红色资源给予高度评价，要求湖南发扬革命传统，传', '', 1, 0, 0, 10),
(12, 5, '0', 1624176605, 'p', 1, 1, 0, 95, 0, '把握历史大势 掌握历史主动', '', '', '曲青山', '《求是》', '/A067477/uploads/210620/1-21062016102M25.jpg', 1624176605, 1624176649, 1, '把握,历史,大势,掌握,主动,把握,历史,大势,', 0, 0, 0, 0, 0, 0, '习近平总书记在党史学习教育动员大会上强调指出：“历史发展有其规律，但人在其中不是完全消极被动的。只要把握住历史发展规律和大势，抓住历史变革时机，顺势而为，奋发有为', '', 1, 0, 0, 11),
(13, 5, '0', 1624176653, 'p', 1, 1, 0, 57, 0, '从新中国史中汲取继续前进的智慧和力量', '', '', '姜 辉', '《求是》', '/A067477/uploads/210620/1-210620161144203.jpg', 1624176653, 1624176712, 1, '从新,中国史,中,汲取,继续,前进,的,智慧,和,', 0, 0, 0, 0, 0, 0, '70多年的新中国史，是一部中国共产党在一个社会主义大国长期执政和治国理政、持续推进伟大社会革命的壮阔史诗，也是一部党团结带领人民筚路蓝缕、奠基立业、艰苦奋斗、改天换地', '', 1, 0, 0, 12),
(14, 5, '0', 1624176739, 'p', 1, 1, 0, 66, 0, '学习社会主义发展史 不断坚定“四个自信”', '', '', '张 磊', '《求是》', '/A067477/uploads/210620/1-210620161305J4.jpg', 1624176739, 1624176804, 1, '学习,社会主义,发展史,不断,坚定,“,学习,', 0, 0, 0, 0, 0, 0, '学习党史要同学习社会主义发展史结合起来，通过学习社会主义发展史、中国共产党追求社会主义事业100年奋进史、中国社会主义发展70多年创业史、特别是改革开放以来中国特色社会主', '', 1, 0, 0, 13),
(15, 5, '0', 1624176807, 'p', 1, 1, 0, 134, 0, '从党的百年历史中汲取继续前进的智慧和力量', '', '', '曲青山', '《求是》', '/A067477/uploads/210620/1-210620161450W0.jpg', 1624176807, 1624176897, 1, '从,党的,百,年历史,中,汲取,继续,前进,的,从,', 0, 0, 0, 0, 0, 0, '今年是中国共产党成立一百周年。党中央决定，在全党开展党史学习教育。这是以习近平同志为核心的党中央作出的一项重大决策。', '', 1, 0, 0, 14),
(16, 5, '0', 1624176901, 'p', 1, 1, 0, 156, 0, '用正确历史观看百年党史', '', '', '中国历史研究院', '《求是》', '/A067477/uploads/210620/1-210620161554H5.jpg', 1624176901, 1624176988, 1, '用,正确,历史,观看,百年,党史,用,正确,历史,', 0, 0, 0, 0, 0, 0, '2021年是伟大的中国共产党成立100周年。习近平总书记深刻指出：“历史，总是在一些特殊年份给人们以汲取智慧、继续前行的力量。”', '', 1, 0, 0, 15),
(17, 5, '0', 1624176992, 'p', 1, 1, 0, 120, 0, '贯彻新发展理念是检验践行“两个维护”的一个', '', '', '冷 溶', '《求是》', '/A067477/uploads/210620/1-210620161S4W7.jpg', 1624176992, 1624177119, 1, '贯彻,新发展,理念,是,检验,践行,“,两个维护,', 0, 0, 0, 0, 0, 0, '贯彻新发展理念作为我国现代化建设的重要指导原则，是推动经济高质量发展、完成“十四五”规划目标任务进而推动民族复兴伟业的制胜法宝。', '', 1, 0, 0, 16),
(18, 5, '0', 1624177123, 'p', 1, 1, 0, 147, 0, '抗疫彰显中华优秀传统文化的强大力量', '', '', '谢伏瞻', '《求是》', '/A067477/uploads/210620/1-210620162046244.jpg', 1624177123, 1624177256, 1, '抗疫,彰显,中华,优秀,传统文化,的,强大,力量,', 0, 0, 0, 0, 0, 0, '在全国抗击新冠肺炎疫情表彰大会上，习近平总书记指出：“抗疫斗争伟大实践再次证明，社会主义核心价值观、中华优秀传统文化所具有的强大精神动力，是凝聚人心、汇聚民力的强', '', 1, 0, 0, 17),
(19, 5, '0', 1624177260, 'p', 1, 1, 0, 91, 0, '中国共产党是抗日战争的中流砥柱', '', '', '曲青山', '《求是》', '/A067477/uploads/210620/1-210620162153Q7.jpg', 1624177260, 1624177336, 1, '中国共产党,是,抗日战争,的,中流砥柱,2020年,', 0, 0, 0, 0, 0, 0, '2020年9月3日下午，习近平总书记在纪念中国人民抗日战争暨世界反法西斯战争胜利75周年座谈会上发表重要讲话。', '', 1, 0, 0, 18),
(20, 6, '0', 1624177724, 'p', 1, 1, 0, 169, 0, '旗帜鲜明讲政治、保证党的团结和集中统一是党', '', '', '同 心', '《求是》', '/A067477/uploads/210620/1-21062016292N54.jpg', 1624177724, 1624177773, 1, '旗帜,鲜,明讲,政治,、,保证,党的,团结,和,集,', 0, 0, 0, 0, 0, 0, '“旗帜鲜明讲政治、保证党的团结和集中统一是党的生命，也是我们党能成为百年大党、创造世纪伟业的关键所在。”', '', 1, 0, 0, 19),
(21, 6, '0', 1624177776, 'p', 1, 1, 0, 75, 0, '鼓起迈进新征程、奋进新时代的精气神', '', '', '同 心', '《求是》', '/A067477/uploads/210620/1-2106201630125W.jpg', 1624177776, 1624177853, 1, '鼓起,迈进,新征程,新,征程,、,奋进,新时代,的,', 0, 0, 0, 0, 0, 0, '“坚持革命继吾志，誓将真理传人寰！”这是夏明翰烈士在狱中写给妻子的家书；江善忠烈士留下血书，“死到阴间不反水，保护共产党万万年”……这些气冲霄汉的豪迈誓言，正是中', '', 1, 0, 0, 20),
(22, 6, '0', 1624245825, 'p,f', 1, 1, 0, 59, 0, '思想之光照亮脱贫征途', '', '', '同 心', '《求是》', '/A067477/uploads/210620/1-21062016315E91.jpg', 1624245825, 1624177931, 1, '思想,之光,照亮,脱贫,征途,思想,之光,照亮,', 0, 0, 0, 0, 0, 0, '历史，又一次醒目地铭刻中华民族伟大复兴进程的新里程碑。', '', 1, 0, 0, 21),
(23, 6, '0', 1624177934, 'p', 1, 1, 0, 108, 0, '正义必胜', '', '', '巨力', '《求是》', '/A067477/uploads/210620/1-210620163321P1.jpg', 1624177934, 1624178020, 1, '正义,必胜,抗美援朝,战争,是,正义,与,强权,、,', 0, 0, 0, 0, 0, 0, '抗美援朝战争是正义与强权、公理与霸凌、进步与倒退的激烈碰撞和对决，是中华民族反抗侵略与霸权的正义之战。', '', 1, 0, 0, 22),
(24, 6, '0', 1624178023, 'p', 1, 1, 0, 72, 0, '坚强的领导核心 科学的思想武器', '', '', '同 心', '《求是》', '/A067477/uploads/210620/1-2106201634521Z.jpg', 1624178023, 1624178099, 1, '坚,强的,领导,核心,科,学的,思想,武器,在,', 0, 0, 0, 0, 0, 0, '“在过去8个多月时间里，我们党团结带领全国各族人民，进行了一场惊心动魄的抗疫大战，经受了一场艰苦卓绝的历史大考，付出巨大努力，取得抗击新冠肺炎疫情斗争重大战略成果，', '', 1, 0, 0, 23),
(25, 6, '0', 1624178104, 'p', 1, 1, 0, 78, 0, '站在百年目标的门槛上', '', '', '同 心', '《求是》', '/A067477/uploads/210620/1-210620163601Z3.jpg', 1624178104, 1624178166, 1, '站在,百年,目,标的,门槛,上,又是,一个,', 0, 0, 0, 0, 0, 0, '又是一个心潮澎湃的历史时刻。2020年7月1日，中国共产党迎来成立99周年的光荣纪念日，站立在全面建成小康社会第一个百年奋斗目标的门槛上。', '', 1, 0, 0, 24),
(26, 6, '0', 1624178179, 'p', 1, 1, 0, 128, 0, '不忘初心、牢记使命与中国共产党的自我革命', '', '', '巨力', '《求是》', '/A067477/uploads/210620/1-210620163I5X1.jpg', 1624178179, 1624178265, 1, '不忘,初心,、,牢记,使命,与,中国,共产,党的,', 0, 0, 0, 0, 0, 0, '中国共产党迎来了99岁华诞。进入百年之境的大党，生机更加旺盛，步履更加豪迈。', '', 1, 0, 0, 25),
(27, 6, '0', 1624178269, 'p', 1, 1, 0, 171, 0, '民族精神：中华民族奋勇前行的不竭动力', '', '', '巨力', '《求是》', '/A067477/uploads/210620/1-210620163T1c8.jpg', 1624178269, 1624178327, 1, '民族,精神,中华民族,奋勇,前,行的,不竭,动力,', 0, 0, 0, 0, 0, 0, '“愿以吾辈之青春，守护盛世之中华。”这是不幸牺牲的山东大学齐鲁医院护士张静静的座右铭。为战“疫”需要而舍弃一头秀发，战“疫”斗志却昂扬生长。', '', 1, 0, 0, 26),
(28, 6, '0', 1624178331, 'p', 1, 1, 0, 171, 0, '中国一定能战胜困难赢得“双胜利”', '', '', '同 心', '《求是》', '/A067477/uploads/210620/1-2106201639495c.jpg', 1624178331, 1624178405, 1, '中国,一,定能,战胜,困难,赢得,“,双胜利,”,', 0, 0, 0, 0, 0, 0, '3月29日，春暖花开时节，习近平总书记来到浙江考察调研，当天下午，他即赴生产建设第一线，了解企业复工复产情况。', '', 1, 0, 0, 27),
(29, 7, '0', 1624178435, 'p,f', 1, 1, 0, 155, 0, '用马克思主义之箭射中国革命之的', '', '', '求是杂志社调研组', '《求是》', '/A067477/uploads/210620/1-210620164155608.jpg', 1624178435, 1624178520, 1, '用,马克思主义,之箭,射,中国革命,之,的,用,', 0, 0, 0, 0, 0, 0, '思想就是力量。', '', 1, 0, 0, 28),
(30, 7, '0', 1624178526, 'p', 1, 1, 0, 146, 0, '努力绘就高水平全面小康的“苏州画卷”', '', '', '求是杂志社、中共', '《求是》', '/A067477/uploads/210620/1-210620164254W1.jpg', 1624178526, 1624178587, 1, '努力,绘,就,高水平,全面,小康,的,“,苏州画卷,', 0, 0, 0, 0, 0, 0, '苏州，是邓小平同志“小康之家”构想印证之地。', '', 1, 0, 0, 29),
(31, 7, '0', 1624178594, 'p', 1, 1, 0, 150, 0, '一条热线撬动的“治理革命”', '', '', '中国人民大学、北', '《求是》', '/A067477/uploads/210620/1-21062016435JA.jpg', 1624178594, 1624178650, 1, '一条,热线,撬动,的,“,治理革命,”,一条,热线,', 0, 0, 0, 0, 0, 0, '北京市坚持以习近平新时代中国特色社会主义思想为指导，深入贯彻习近平总书记对北京重要讲话精神，从2019年开始推进“接诉即办”改革，用一条热线撬动“治理革命”，形成落实以', '', 1, 0, 0, 30),
